import React, { useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios"

export default function Post() {
  let { id } = useParams();

  useEffect(() => {
      axios.get("http://localhost:3001/posts/byId/${id}").then(
          (response) => {
              console.log(response)
          }
      );
  })
  return (
    <div>
      <h1>User id is {id}</h1>
    </div>
  );
};

